import React, {useContext, useEffect, useState} from 'react';
import {Col, Container, Row} from "react-bootstrap";
import GenusBar from "../components/GenusBar";
import ProductList from "../components/ProductList";
import {Context} from "../index";
import {getGenus, receiveProducts, receiveProductsGenuId} from "../http/productAPI";
import {observer} from "mobx-react-lite";
import ProductItem from "../components/ProductItem";

const HomePage = observer(() => {
    const {product} = useContext(Context)
    const [selectedGenus, setSetectedGenus] = useState(product.selectedGenus.id)

    useEffect(() => {
        getGenus().then(data => product.setGenus(data))
        receiveProducts(1).then(data => {
            product.setProducts(data.rows)
        })
    }, [])

    useEffect(() => {
        receiveProducts(selectedGenus).then(data => {
            product.setProducts(data.rows)
        })
    }, [product.selectedGenus])

    return (
        <Container>
            <Row className="mt-3">
                <Col md={3}>
                    <GenusBar/>
                </Col>
                <Col md={9}>
                    <ProductList/>
                </Col>
            </Row>
        </Container>
    );
});

export default HomePage;
