import React from 'react';
import {Col, Container, Form, Image, Row} from "react-bootstrap";

const ProductPage = () => {
    const product = {id: 1, title: 'Какаду белохохлый', information: 'infoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoin foinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfo infoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfoinfo',price: 60000, img: 'https://m.mirkorma.ru/upload/medialibrary/313/313b9efabeb8a8d4ed21f1a7f610c350.jpg'}
    return (
        <Container className="mt-3">
            <Row>
                <Col md={3}>
                    <Image width={300} height={300} src={product.img}/>
                </Col>
                <Col md={8} className="d-lg-block">
                    <h1>{product.title}</h1>
                    <div style={{}}>
                        <text style={{fontSize:26}}>{product.information}</text>
                    </div>
                    <text style={{fontSize:26}}>{product.price}</text>
                </Col>
            </Row>
        </Container>
    );
};

export default ProductPage;
