import {$authHost, $host} from "./index";
import "../components/modals/createProduct";

export const createProduct = async (product) => {
    const {data} = await $authHost.post('api/product', product)
    return data
}
export const receiveProducts = async () => {
    const {data} = await $host.get('api/product')
    return data
}
export const receiveProductsGenuId = async (genuId) => {
    const {data} = await $host.get('api/product/genuId', {params: {genuId}})
    return data
}
export const receiveProductsUserId = async (userId) => {
    const {data} = await $host.get('api/product/userId', {params: {userId}})
    return data
}
export const receiveOneProduct = async (id) => {
    const {data} = await $host.get('api/product/' + id)
    return data
}
export const getGenus = async () => {
    const {data} = await $host.get('api/genus')
    return data
}
