import React, {useContext, useEffect, useState} from 'react';
import {Button, Dropdown, Form, Modal} from "react-bootstrap";
import {Context} from "../../index";
import {createProduct, getGenus} from "../../http/productAPI";
import {observer} from "mobx-react-lite";
import {toFormData} from "axios";
import {createInformation, getUser} from "../../http/userAPI";
import jwtDecode from "jwt-decode";

const addInformation = observer(({show, onHide}) => {

    const user = useContext(Context)
    const token = jwtDecode(localStorage.getItem('token'))
    useEffect(() => {
        getUser(token.id).then(data => user.setUser(data))
    }, [])


    const [fname, setFname] = useState('')
    const [sname, setSname] = useState('')
    const [lname, setLname] = useState('')
    const [phoneNumber, setPhoneNumber] = useState('')

    const addInfo = async () => {
        console.log(user)
        // createInformation(user.id, fname, sname, lname, phoneNumber).then(data => onHide())
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Изменить информацию
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        placeholder={"Фамилия"}
                        value={sname}
                        onChange={e => setSname(e.target.value)}/>
                    <Form.Control
                        className="mt-2"
                        style={{height: 300}}
                        placeholder={"Имя"}
                        value={fname}
                        onChange={e => setFname(e.target.value)}
                    />
                    <Form.Control
                        className="mt-2" type="number"
                        placeholder={"Отчество"}
                        value={lname}
                        onChange={e => setLname(e.target.value)}
                    />
                    <Form.Control
                        className="mt-2" type="number"
                        placeholder={"Номер телефона"}
                        value={phoneNumber}
                        onChange={e => setPhoneNumber(e.target.value)}
                    />
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addInfo}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default addInformation;
