import React, {useContext, useState} from 'react';
import {Button, Dropdown, Form, Modal} from "react-bootstrap";
import {Context} from "../../index";

const CreateProduct = ({show, onHide}) => {
    const {product} = useContext(Context)

    const [title, setTitle] = useState('')
    const [information, setInformation] = useState('')

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить попугая
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control placeholder={"Заголовок"}/>
                    <Dropdown className="mt-2">
                        <Dropdown.Toggle>Семейство</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {product.genus.map(genus =>
                                <Dropdown.Item key={genus.id}>{genus.title}</Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control className="mt-2" style={{height: 300}} placeholder={"Информация"}/>
                    <Form.Control className="mt-2" type={"number"} placeholder={"Цена"}/>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={onHide}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateProduct;
